import React, { useState, useEffect } from 'react';
import ReactDataGrid from 'react-data-grid';
import 'react-data-grid/dist/react-data-grid.css';
import { NewItemButton, NewDateButton } from './Buttons.js';
import Cookie from 'js-cookie';


const originalColumn = { key: "item", name: "Item", editable: true };

const Grid = () => {
    let d = Cookie.get("dates");
    if(d == undefined) {
        d = [];
    }
    let i = Cookie.get("items");
    if(i == undefined) {
        i = [];
    }

    let [dates, setDates] = useState(d);
    let [items, setItems] = useState(i);


    // let [dates, setDates] = useState([]);
    // let [items, setItems] = useState([]);

    useEffect(() => {
        Cookie.set("dates", JSON.stringify(dates));
        console.log("dates :" + dates);
    }, [dates]);

    useEffect(() => {
        Cookie.set("items", JSON.stringify(items));
        console.log("items :" + items);
    }, [items])

    let columns = [...dates];
    columns.unshift(originalColumn);    

    let addRow = () => {
        const item = prompt("New Item: ");
        let newRow = { item: item };
        let newItems = [...items];
        newItems.push(newRow);
        setItems(newItems);
    };

    const addCol = () => {
        const date = prompt("New Date: ");
        let newCol = { key: date, name: date, editable: true };
        let newCols = [...dates];        
        newCols.push(newCol);
        setDates(newCols);
    };

    let newItem = NewItemButton({addRow});
    let newDate = NewDateButton({addCol});

    const handleRowsUpdate = ({ fromRow, toRow, updated }) => {
        let newItems = [...items];
        for(let i = fromRow; i <= toRow; i++) {
            newItems[i] = { ...newItems[i], ...updated };
        }
        setItems(newItems);
    }

    return (
        <div>
            <ReactDataGrid
                columns={columns}
                rows={items}
                onRowsUpdate={handleRowsUpdate}
                enableCellSelect={true}
                minHeight={150}
            />
            {newItem}
            {newDate}  
        </div>

    )
}


export default Grid;
