    { item: "Klose", "05.07": 100 },
    { item: "scales", "05.07": 200 }
];

const Grid = () => {

    let [columns, setColumns] = useState(originalCols);
    let [rows, setRows] = useState([]);

    let addRow = () => {};
    let addCol = () => {};

    return (
        <div>
            <ReactDataGrid
                columns={columns}
                rows={rows}
                rowsCount={2}
                minHeight={150}
            />
            <NewItemButton addRow={addRow} />
            <NewDateButton addCol={addCol} />        
        </div>

    )
}

export default Grid;
