import React, { useState } from 'react';
import ReactDataGrid from 'react-data-grid';
import 'react-data-grid/dist/react-data-grid.css';
import { NewItemButton, NewDateButton } from './Buttons.js';


const originalColumn = { key: "item", name: "Item" };

// const rows = [
//     { item: "Klose", "05.07": 100 },
//     { item: "scales", "05.07": 200 }
// ];

const Grid = () => {

    let [dates, setDates] = useState([]);
    let [items, setItems] = useState([]);

    let columns = [...dates];
    columns.unshift(originalColumn);    

    let addRow = () => {
        const item = prompt("New Item: ");
        let newRow = { item: item };
        let newItems = [...items];
        newItems.push(newRow);
        setItems(newItems);
    };

    const addCol = () => {
        const date = prompt("New Date: ");
        let newCol = { key: date, name: date, editable: true };
        let newCols = [...dates];        
        newCols.push(newCol);
        setDates(newCols);
    };

    let newItem = NewItemButton({addRow});
    let newDate = NewDateButton({addCol});

    const onGridRowsUpdated = ({ fromRow, toRow, updated }) => {
        console.log(fromRow);
        console.log(toRow);
        console.log(updated);
    }

    return (
        <div>
            <ReactDataGrid
                columns={columns}
                rows={items}
                rowsCount={items.length}
                onGridRowsUpdated={onGridRowsUpdated}
                enableCellSelect={true}
                minHeight={150}
            />
            {newItem}
            {newDate}  
        </div>

    )
}

export default Grid;
